import React, { useState } from "react";

export default function Task({ name, id, tasks, setTasks }) {
  function deleteTask(id) {
    setTasks(tasks.filter((task) => task.id !== id));
  }
  return (
    <div className="taskContainer">
      <div className="priority"></div>
      <div className="taskDescription">{name}</div>
      <button className="taskDone" value={id} onClick={deleteTask(this.value)}>
        X
      </button>
    </div>
  );
}
