import React, { useState } from "react";

export default function SettingButtons({ tasks, setTasks }) {
  function prioritySort() {
    let low = [];
    let normal = [];
    let high = [];

    tasks.forEach(function (task) {
      if (task.priority === "low") {
        low.push(task);
      } else if (task.priority === "normal") {
        normal.push(task);
      } else if (task.priority === "high") {
        high.push(task);
      }
    });

    setTasks([...high, ...normal, ...low]);
  }

  function completionSort() {
    let complete = [];
    let incomplete = [];

    tasks.forEach(function (task) {
      if (task.status === true) {
        complete.push(task);
      } else {
        incomplete.push(task);
      }
    });

    setTasks([...incomplete, ...complete]);
  }

  function default1() {
    setTasks([
      {
        name: "eat first meal",
        id: Math.random(),
        priority: "high",
        status: false,
      },
      {
        name: "eat second meal",
        id: Math.random(),
        priority: "high",
        status: false,
      },
      {
        name: "go outside",
        id: Math.random(),
        priority: "high",
        status: false,
      },
      {
        name: "code",
        id: Math.random(),
        priority: "high",
        status: false,
      },
      {
        name: "exercise",
        id: Math.random(),
        priority: "high",
        status: false,
      },
      {
        name: "talk to Vivien",
        id: Math.random(),
        priority: "high",
        status: false,
      },
      {
        name: "juggle",
        id: Math.random(),
        priority: "normal",
        status: false,
      },
      {
        name: "yoyo",
        id: Math.random(),
        priority: "normal",
        status: false,
      },
      {
        name: "genshin: daily artifacts",
        id: Math.random(),
        priority: "low",
        status: false,
      },
      {
        name: "genshin: daily crystal ores",
        id: Math.random(),
        priority: "low",
        status: false,
      },
      {
        name: "play ssbm",
        id: Math.random(),
        priority: "low",
        status: false,
      },
      {
        name: "play tetris",
        id: Math.random(),
        priority: "low",
        status: false,
      },
    ]);
  }

  return (
    <div className="sortingButtonsContainer">
      <button className="prioritySortButton" onClick={prioritySort}>
        Sort By Priority
      </button>
      <button className="prioritySortButton" onClick={completionSort}>
        Sort By Completion
      </button>
      <button className="default1Button" onClick={default1}>
        Default 1
      </button>
    </div>
  );
}
