import React, { useState } from "react";

export default function Weather() {
  //use state to get input from form
  const [city, setCity] = useState("Salisbury");

  function displayWeather() {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=28144&appid=26ce245088c3cd75b76abae32afa318b`
    )
      .then((response) => response.json())
      .then((data) => console.log(data))
      .catch((err) => console.log("wrong city name"));
  }

  return (
    <div className="weatherContainer">
      <div className="weatherInputContainer">
        <input
          type="text"
          placeholder="enter a city"
          className="inputValue"
          onChange={setCity(this.value)}
        ></input>
        <input type="submit" value="submit" onClick={displayWeather}></input>
      </div>
      <div className="weatherDisplayContainer">
        <div className="location">city</div>
        <div className="temperature"></div>
        <div className="weatherDescription"></div>
      </div>
    </div>
  );
}
