import React, { useState, useEffect } from "react";
import TaskList from "./TaskList.js";

export default function NewListModal({ lists, setLists }) {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState("");
  const [priority, setPriority] = useState("normal");

  function addTask() {
    setTasks([
      ...tasks,
      {
        name: task,
        id: Math.random(),
        priority: priority,
        status: false,
      },
    ]);
  }

  function submitList() {
    setLists([...lists, tasks]);
  }
  return (
    <div className="newListModalContainer">
      <input type="text" placeholder="list name"></input>
      <input
        type="text"
        placeholder="add new list item"
        onChange={(e) => setTask(e.target.value)}
      ></input>

      <input type="submit" placeholder="add" onClick={addTask}></input>
      <select id="browsers" onChange={(e) => setPriority(e.target.value)}>
        <option value="low">low</option>
        <option selected="selected" value="normal">
          normal
        </option>
        <option value="high">high</option>
      </select>
      <input type="submit" placeholder="done" onClick={submitList}></input>
      <TaskList tasks={tasks} setTasks={setTasks} />
    </div>
  );
}
