import React, { useState } from "react";

export default function Task({ name, id, tasks, setTasks }) {
  function deleteTask(e) {
    //console.log(e.target.value);

    let b = tasks;
    console.log(b);

    for (let i = 0; i < b.length; i++) {
      if (Number(b[i].id) === Number(e.target.value)) {
        b.splice(i, 1);
        i--;
      }
    }
    console.log(b);
    console.log(tasks);

    setTasks(b);
    //console.log(tasks);
    // setTasks(tasks.pop());
  }
  return (
    <div className="taskContainer">
      <div className="priority"></div>
      <div className="taskDescription">{name}</div>
      <button className="taskDone" value={id} onClick={(e) => deleteTask(e)}>
        X
      </button>
    </div>
  );
}
