import React from "react";
import List from "./Lists.js";

export default function Lists({ lists, setTasks }) {
  function changeList() {
    setTasks();
  }
  return (
    <div className="listsContainer">
      {lists.map((list) => (
        <div onClick={() => changeList} data-tasks={JSON.stringify(list.tasks)}>
          {list.name}
        </div>
      ))}
    </div>
  );
}
