import React, { useState } from "react";

export default function Task({ name, id, tasks, setTasks }) {
  function deleteTask(e) {
    console.log(e.target.value);
    console.log(
      tasks.filter(function (task) {
        if (task.id === e.target.value) {
          return task;
        }
      })
    );

    setTasks((tasks = []));
    console.log(tasks);
    // setTasks(tasks.pop());
  }
  return (
    <div className="taskContainer">
      <div className="priority"></div>
      <div className="taskDescription">{name}</div>
      <button className="taskDone" value={id} onClick={(e) => deleteTask(e)}>
        X
      </button>
    </div>
  );
}
