import React, { useState } from "react";

export default function Task({
  task,
  name,
  id,
  tasks,
  setTasks,
  priority,
  status,
  setStatus,
}) {
  function deleteTask(e) {
    setTasks((tasks) => tasks.filter((ev) => ev.id !== task.id));
  }

  function completeTask(e) {
    let taskList = document.querySelectorAll(".taskContainer");
    setStatus("complete");
    tasks.foreach(function (ev) {
      if (ev.id === task.id) {
        setStatus("complete");
      }
    });
  }

  return (
    <div className="taskContainer" value={id}>
      <div className="priority">{priority}</div>
      <div className="taskDescription">{name}</div>
      <div className="taskButtonContainer">
        <button className="taskDone" value={id} onClick={(e) => deleteTask(e)}>
          X
        </button>
        <button
          className="Complete"
          value={id}
          onClick={(e) => completeTask(e)}
        >
          Complete
        </button>
      </div>
    </div>
  );
}
