import React, { useState } from "react";

export default function Input({ tasks, setTasks }) {
  const [taskName, setTaskName] = useState("");
  function addTask() {
    console.log(Array.isArray(tasks));
    setTasks({ task: taskName });
    console.log(tasks);
    //.setTask push text values
  }
  return (
    <div className="inputContainer">
      <input
        type="text"
        value={(tasks, taskName)}
        placeholder="add task"
        onChange={(e) => setTaskName(e.target.value)}
      ></input>
      <button className="addButton" onClick={addTask}>
        Add
      </button>
      <button className="priorityButton" onClick={() => console.log(tasks)}>
        dd
      </button>
      <button className="priorityButton"></button>
      <button className="priorityButton"></button>
    </div>
  );
}
