import React, { useState } from "react";

export default function Task({
  task,
  name,
  id,
  tasks,
  setTasks,
  priority,
  status,
  setStatus,
}) {
  function deleteTask() {
    setTasks((tasks) => tasks.filter((ev) => ev.id !== task.id));
  }

  function completeTask() {
    tasks.map((item) => {
      if (item.id === task.id) {
        console.log(item.status);
        return { ...item, status: true };
      }
      return item;
    });
  }

  return (
    <div className="taskContainer" value={id}>
      <div className="priority">{priority}</div>
      <div className="taskDescription">{name}</div>
      <div className="taskButtonContainer">
        <button className="taskDone" value={id} onClick={() => deleteTask()}>
          X
        </button>
        <button className="Complete" value={id} onClick={() => completeTask()}>
          Complete
        </button>
      </div>
    </div>
  );
}
